mapper.update:

UPDATE T_BOARD 
	SET BOARD_SEQ = BOARD_SEQ -1 
	WHERE BOARD_SEQ > #{boardSeq}

mapper.delete:

DELETE FROM T_BOARD_FILE
	WHERE BOARD_SEQ = #{boardSeq}


    AND FILE_SEQ= #{fileSeq}

mapper.update:

UPDATE T_BOARD_FILE
	SET FILE_SEQ = FILE_SEQ - 1
	WHERE BOARD_SEQ = #{boardSeq}
	  AND FILE_SEQ > #{fileSeq}


UPDATE T_BOARD_FILE
	SET BOARD_SEQ = BOARD_SEQ - 1
	WHERE BOARD_SEQ > #{boardSeq}
